    function getStrategies() external view returns (address[] memory);

    function getWithdrawQueue() external view returns (address[] memory);

    function strategy(address _strategy)
        external
        view
        returns (
            bool _active,
            uint256 _interestFee,
            uint256 _debtRate,
            uint256 _lastRebalance,
